// FIELD TO LIST ALL RECORDED SITE VISITS (ANY TYPE)

let dates = REPEATABLEVALUES($service_visit_records, 'visit_date') || [];
dates.sort((a, b) => {
  let aDate = DATEVALUE(a);
  let bDate = DATEVALUE(b);

  if (aDate.valueOf() > bDate.valueOf()) {
    return 1;
  } else if (bDate.valueOf() > aDate.valueOf()) {
    return -1;
  } else {
    return 0;
  }
});

const lastDate = dates[dates.length - 1];

lastDate ? SETRESULT(DATEVALUE(lastDate).toLocaleDateString("en-GB")) : SETRESULT("No service visits have taken place");

// FIELD TO LIST ALL MONITORING VISITS

const dates = REPEATABLEVALUES($service_visit_records, 'visit_date') || [];
const types = (REPEATABLEVALUES($service_visit_records, 'visit_type') || []).map(CHOICEVALUE);

const matchedDates = dates.reduce((r, date, i) => {
  if (types[i] === "Monitoring") {
    r.push(date);
  }
  return r;
}, []);

matchedDates.sort((a, b) => {
  let aDate = DATEVALUE(a);
  let bDate = DATEVALUE(b);

  if (aDate.valueOf() > bDate.valueOf()) {
    return 1;
  } else if (bDate.valueOf() > aDate.valueOf()) {
    return -1;
  } else {
    return 0;
  }
});

const lastDate = matchedDates[matchedDates.length - 1];

lastDate ? SETRESULT(DATEVALUE(lastDate).toLocaleDateString("en-GB")) : SETRESULT("No monitoring visits have taken place");

const dates = REPEATABLEVALUES($service_visit_records, "visit_date") || [];
const visit_type_knotweed_application = (
  REPEATABLEVALUES(
    $service_visit_records,
    "visit_type_japanese_knotweed_application_monitoring"
  ) || []
).map(CHOICEVALUE);
const visit_type_knotweed_other = (
  REPEATABLEVALUES(
    $service_visit_records,
    "visit_type_japanese_knotweed_other"
  ) || []
).map(CHOICEVALUE);
const visit_type_invasive_application = (
  REPEATABLEVALUES(
    $service_visit_records,
    "visit_type_invasive_plants_application"
  ) || []
).map(CHOICEVALUE);
const visit_type_invasive_other = (
  REPEATABLEVALUES(
    $service_visit_records,
    "visit_type_invasive_plants_other"
  ) || []
).map(CHOICEVALUE);
const visit_type_other = (
  REPEATABLEVALUES($service_visit_records, "visit_type_other") || []
).map(CHOICEVALUE);

const visitNumber =
  REPEATABLEVALUES($service_visit_records, "visit_number") || [];

if (dates.length === 0) {
  SETRESULT("No service visits have taken place");
} else {
  const combined = dates.reduce((r, date, i) => {
    // Since these values are mutually exclusive we can just use the first one that is set
    const chosenValue =
      visit_type_knotweed_application[i] ||
      visit_type_knotweed_other[i] ||
      visit_type_invasive_application[i] ||
      visit_type_invasive_other[i] ||
      visit_type_other[i];

    r.push({
      date,
      visitTypeOther: chosenValue,
      visitNumber: visitNumber[i],
    });
    return r;
  }, []);

  combined.sort((a, b) => {
    if (a.visitNumber > b.visitNumber) {
      return 1;
    } else if (b.visitNumber > a.visitNumber) {
      return -1;
    } else {
      return 0;
    }
  });

  const result = combined.reduce(
    (r, entry, i) =>
      (r += `${entry.visitNumber}:  ${new Date(
        entry.date
      ).toLocaleDateString("en-GB")}, ${entry.visitTypeOther}\n`),
    ""
  );

  SETRESULT(result.trim());
}


