const dates = REPEATABLEVALUES($service_visit_records, 'visit_date') || [];
const types = (REPEATABLEVALUES($service_visit_records, 'visit_type_japanese_knotweed_application_monitoring') || []).map(CHOICEVALUE);
const typesOther = (REPEATABLEVALUES($service_visit_records, 'visit_type_japanese_knotweed_other') || []).map(CHOICEVALUE);
const category = (REPEATABLEVALUES($service_visit_records, 'visit_category') || []).map(CHOICEVALUE);
const visitNumber = REPEATABLEVALUES($service_visit_records, "visit_number") || [];

if (category.filter((item) => item === "Japanese Knotweed Management Record").length === 0) {
  SETRESULT("No service visits have taken place");
} else {
  const combined = dates.reduce((r, date, i) => {
    r.push({
      date,
      visitType: types?.[i] ?? typesOther?.[i] ?? "Monitoring",
      visitNumber: visitNumber[i],
      visitCategory: category[i],
    });
    return r;
  }, []);

  combined.sort((a, b) => {    
    if (a.visitNumber > b.visitNumber) {
      return 1;
    } else if (b.visitNumber > a.visitNumber) {
      return -1;
    } else {
      return 0;
    }
  });

  const result = combined.reduce((r, entry, i) => {
    if (entry.visitType && entry.visitCategory === "Japanese Knotweed Management Record") {
      r += `Visit ${entry.visitNumber}, ${new Date(entry.date).toLocaleDateString("en-GB")}, ${entry.visitType}\n`
    }
    return r;
  }, "");

  SETRESULT(result.trim());
}

